from django.urls import pathfrom .views import homepage, about, detail,categories_views, categoryDelete_views, login_views, logout_viewsurlpatterns = [    path('', homepage, name='home'),    path('aboutme', about, name='aboutme'),    path('detail/<int:id>/', detail, name='detail_page'),    path('categories', categories_views, name='categories'),    path('categories-delete/<int:id>', categoryDelete_views, name='category_delete'),    path('login/', login_views, name='login'),    path('logout/', logout_views, name='login'),]